import { useState } from 'react';
import styles from './ListenerPage.module.css';
import MusicIcon from '../../icons/MusicIcon';
import { CheckCircleIcon } from '../../icons/CheckIcon';
import FolderIcon from '../../icons/FolderIcon';
import SearchIcon from '../../icons/SearchIcon';

const StudentPage = () => {
    const [isListening, setIsListening] = useState(false);

    const handleStartListening = () => {
        setIsListening(true);
        // –ó–¥–µ—Å—å –º–æ–∂–Ω–æ –¥–æ–±–∞–≤–∏—Ç—å –ª–æ–≥–∏–∫—É –ø–æ–¥–∫–ª—é—á–µ–Ω–∏—è –∫ —Å–µ—Ä–≤–µ—Ä—É
    };

    const handleStopListening = () => {
        setIsListening(false);
        // –ó–¥–µ—Å—å –º–æ–∂–Ω–æ –¥–æ–±–∞–≤–∏—Ç—å –ª–æ–≥–∏–∫—É –æ—Ç–∫–ª—é—á–µ–Ω–∏—è –æ—Ç —Å–µ—Ä–≤–µ—Ä–∞
    };

    const handleRefresh = () => {
        // –õ–æ–≥–∏–∫–∞ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è
    };

    return (
        <div className={styles.container}>
            {/* –°–∞–π–¥–±–∞—Ä - —É–ø—Ä–æ—â–µ–Ω–Ω–∞—è –≤–µ—Ä—Å–∏—è –±–µ–∑ –≤—ã–±–æ—Ä–∞ –ª–µ–∫—Ü–∏–∏ */}
            <div className={styles.sidebar}>
                <div className={styles.sidebarSection}>
                    <div className={styles.sidebarTitle}>
                        <MusicIcon /> –ù–∞—Å—Ç—Ä–æ–π–∫–∏ —Å–ª—É—à–∞—Ç–µ–ª—è
                    </div>
                </div>

                <div className={styles.sidebarSection}>
                    <div className={styles.sidebarTitle}>
                        –°–µ—Ä–≤–µ—Ä
                    </div>
                    <div className={styles.serverInfo}>
                        <div className={styles.serverAddress}>51.250.115.73:8000</div>
                        <div className={`${styles.statusIndicator} ${styles.statusActive}`}>
                            <CheckCircleIcon /> –°–µ—Ä–≤–µ—Ä –¥–æ—Å—Ç—É–ø–µ–Ω
                        </div>
                    </div>

                    <div className={styles.statusIndicator}>
                        <span>MongoDB</span>
                        <span className={styles.statusActive}><CheckCircleIcon /></span>
                    </div>

                    <div className={styles.statusIndicator}>
                        <span>API –∫–ª—é—á–∏</span>
                        <span className={styles.statusActive}><CheckCircleIcon /></span>
                    </div>

                    <div className={styles.performanceMetric}>
                        <span>–ê–∫—Ç–∏–≤–Ω—ã–µ —Å–µ—Å—Å–∏–∏</span>
                        <span>0</span>
                    </div>

                    <div className={styles.performanceMetric}>
                        <span>WebSocket</span>
                        <span>0</span>
                    </div>
                </div>

                <div className={styles.sidebarSection}>
                    <div className={styles.sidebarTitle}>
                        <FolderIcon /> –í—ã–±–æ—Ä –ª–µ–∫—Ü–∏–∏
                    </div>
                    <div className={styles.emptyState}>
                        <SearchIcon /> –°–µ–π—á–∞—Å –Ω–µ—Ç –∞–∫—Ç–∏–≤–Ω—ã—Ö –ª–µ–∫—Ü–∏–π
                    </div>

                    {/* <div className={styles.lectureArchive}>
                        <div className={styles.sidebarTitle}>
                            <span>üìö</span> –ó–∞–ø–∏—Å–∏ –ª–µ–∫—Ü–∏–π:
                        </div>
                        {lectures.length === 0 ? (
                            <div className={styles.emptyState}>–ù–µ—Ç –¥–æ—Å—Ç—É–ø–Ω—ã—Ö –∑–∞–ø–∏—Å–µ–π</div>
                        ) : (
                            <ul>
                                {lectures.map(lecture => (
                                    <li
                                        key={lecture.id}
                                        className={`${styles.lectureItem} ${selectedLecture === lecture.id ? styles.selected : ''}`}
                                        onClick={() => handleLectureSelect(lecture.id)}
                                    >
                                        {lecture.title}
                                    </li>
                                ))}
                            </ul>
                        )}
                    </div> */}
                </div>
            </div>

            {/* –û—Å–Ω–æ–≤–Ω–æ–µ —Å–æ–¥–µ—Ä–∂–∏–º–æ–µ */}
            <div className={styles.content}>
                <div className={styles.header}>
                    <h1 className={styles.title}>–°–ª—É—à–∞—Ç–µ–ª—å –ª–µ–∫—Ü–∏–∏</h1>
                    <h2 className={styles.subtitle}>
                        {isListening
                            ? '–ò–¥–µ—Ç –ø—Ä–æ—Å–ª—É—à–∏–≤–∞–Ω–∏–µ —Ç–µ–∫—É—â–µ–π –ª–µ–∫—Ü–∏–∏...'
                            : '–ì–æ—Ç–æ–≤–æ –∫ –ø—Ä–æ—Å–ª—É—à–∏–≤–∞–Ω–∏—é —Ç–µ–∫—É—â–µ–π –ª–µ–∫—Ü–∏–∏'}
                    </h2>
                </div>

                <div className={styles.section}>
                    <div className={styles.buttonGroup}>
                        <button
                            onClick={handleStartListening}
                            disabled={isListening}
                            className={`${styles.button} ${styles.buttonPrimary}`}
                        >
                            –ù–∞—á–∞—Ç—å –ø—Ä–æ—Å–ª—É—à–∏–≤–∞–Ω–∏–µ
                        </button>
                        <button
