import { useState } from 'react';
import styles from './ListenerPage.module.css';
import MusicIcon from '../../icons/MusicIcon';
import { CheckCircleIcon } from '../../icons/CheckIcon';
import FolderIcon from '../../icons/FolderIcon';
import SearchIcon from '../../icons/SearchIcon';

const StudentPage = () => {
    const [isListening, setIsListening] = useState(false);

    const handleStartListening = () => {
        setIsListening(true);
        // Здесь можно добавить логику подключения к серверу
    };

    const handleStopListening = () => {
        setIsListening(false);
        // Здесь можно добавить логику отключения от сервера
    };

    const handleRefresh = () => {
        // Логика обновления
    };

    return (
        <div className={styles.container}>
            {/* Сайдбар - упрощенная версия без выбора лекции */}
            <div className={styles.sidebar}>
                <div className={styles.sidebarSection}>
                    <div className={styles.sidebarTitle}>
                        <MusicIcon /> Настройки слушателя
                    </div>
                </div>

                <div className={styles.sidebarSection}>
                    <div className={styles.sidebarTitle}>
                        Сервер
                    </div>
                    <div className={styles.serverInfo}>
                        <div className={styles.serverAddress}>51.250.115.73:8000</div>
                        <div className={`${styles.statusIndicator} ${styles.statusActive}`}>
                            <CheckCircleIcon /> Сервер доступен
                        </div>
                    </div>

                    <div className={styles.statusIndicator}>
                        <span>MongoDB</span>
                        <span className={styles.statusActive}><CheckCircleIcon /></span>
                    </div>

                    <div className={styles.statusIndicator}>
                        <span>API ключи</span>
                        <span className={styles.statusActive}><CheckCircleIcon /></span>
                    </div>

                    <div className={styles.performanceMetric}>
                        <span>Активные сессии</span>
                        <span>0</span>
                    </div>

                    <div className={styles.performanceMetric}>
                        <span>WebSocket</span>
                        <span>0</span>
                    </div>
                </div>

                <div className={styles.sidebarSection}>
                    <div className={styles.sidebarTitle}>
                        <FolderIcon /> Выбор лекции
                    </div>
                    <div className={styles.emptyState}>
                        <SearchIcon /> Сейчас нет активных лекций
                    </div>

                    {/* <div className={styles.lectureArchive}>
                        <div className={styles.sidebarTitle}>
                            <span>📚</span> Записи лекций:
                        </div>
                        {lectures.length === 0 ? (
                            <div className={styles.emptyState}>Нет доступных записей</div>
                        ) : (
                            <ul>
                                {lectures.map(lecture => (
                                    <li
                                        key={lecture.id}
                                        className={`${styles.lectureItem} ${selectedLecture === lecture.id ? styles.selected : ''}`}
                                        onClick={() => handleLectureSelect(lecture.id)}
                                    >
                                        {lecture.title}
                                    </li>
                                ))}
                            </ul>
                        )}
                    </div> */}
                </div>
            </div>

            {/* Основное содержимое */}
            <div className={styles.content}>
                <div className={styles.header}>
                    <h1 className={styles.title}>Слушатель лекции</h1>
                    <h2 className={styles.subtitle}>
                        {isListening
                            ? 'Идет прослушивание текущей лекции...'
                            : 'Готово к прослушиванию текущей лекции'}
                    </h2>
                </div>

                <div className={styles.section}>
                    <div className={styles.buttonGroup}>
                        <button
                            onClick={handleStartListening}
                            disabled={isListening}
                            className={`${styles.button} ${styles.buttonPrimary}`}
                        >
                            Начать прослушивание
                        </button>
                        <button
                            onClick={handleStopListening}
                            disabled={!isListening}
                            className={`${styles.button} ${styles.buttonDanger}`}
                        >
                            Остановить прослушивание
                        </button>
                        <button
                            onClick={handleRefresh}
                            className={`${styles.button} ${styles.buttonSecondary}`}
                        >
                            Обновить
                        </button>
                    </div>
                </div>

                <div className={styles.divider}></div>

                <div className={styles.section}>
                    <h3 className={styles.sectionTitle}>Как использовать:</h3>
                    <ol>
                        <li><strong>Нажмите "Начать прослушивание"</strong></li>
                        <li>Следите за текстом в реальном времени</li>
                        <li>Остановите когда захотите</li>
                    </ol>
                </div>

                <div className={styles.divider}></div>

                <div className={styles.section}>
                    <h3 className={styles.sectionTitle}>Преимущества режима слушателя:</h3>
                    <ul className={styles.featureList}>
                        <li className={styles.featureItem}>
                            <span className={styles.featureCheckbox}></span>
                            Только получение текста (экономия ресурсов)
                        </li>
                        <li className={styles.featureItem}>
                            <span className={styles.featureCheckbox}></span>
                            Быстрое подключение
                        </li>
                        <li className={styles.featureItem}>
                            <span className={styles.featureCheckbox}></span>
                            Автоматическая очистка старых сообщений
                        </li>
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default StudentPage;
