import React from 'react';
import commonStyles from '../commonStyles.module.css';
import { LectureIcon } from '../../icons/LectureIcon';
import { MonitorIcon } from '../../icons/MonitorIcon';
import { SettingsIcon } from '../../icons/SettingsIcon';

const HomePage = () => {
    return (
        <div className={commonStyles.homeContainer}>
            {/* Заголовок */}
            <header className={commonStyles.homeHeader}>
                <h1 className={commonStyles.mainTitle}>Yandex Audio Processing Pipeline</h1>
                <p className={commonStyles.subTitle}>Веб-интерфейс для записи и обработки аудио</p>
            </header>

            {/* Выбор роли */}
            <div className={commonStyles.roleSelection}>
                <h2 className={commonStyles.sectionTitle}>Выберите вашу роль:</h2>
                <div className={commonStyles.roleButtons}>
                    <button className={`${commonStyles.roleButton} ${commonStyles.primaryRoleButton}`}>
                        Я ЛЕКТОР
                    </button>
                    <button className={`${commonStyles.roleButton} ${commonStyles.secondaryRoleButton}`}>
                        Я СЛУШАТЕЛЬ
                    </button>
                </div>
            </div>

            {/* Быстрый доступ */}
            <div className={commonStyles.quickAccess}>
                <h2 className={commonStyles.sectionTitle}>Быстрый доступ:</h2>
                <div className={commonStyles.quickLinks}>
                    <a href="/archive" className={commonStyles.quickLink}>
                        <LectureIcon className={commonStyles.quickLinkIcon} />
                        <span>Архив лекций</span>
                    </a>
                    <a href="/monitoring" className={commonStyles.quickLink}>
                        <MonitorIcon className={commonStyles.quickLinkIcon} />
                        <span>Мониторинг</span>
                    </a>
                    <a href="/management" className={commonStyles.quickLink}>
                        <SettingsIcon className={commonStyles.quickLinkIcon} />
                        <span>Управление</span>
                    </a>
                </div>
            </div>
        </div>
    );
};

export default HomePage;
