import { useState } from 'react';
import { CheckCircleIcon } from '../../icons/CheckIcon';
import { CogIcon } from '../../icons/CogIcon';
import styles from './MonitoringPage.module.css';

const MonitoringPage = () => {
    const [isMonitoring, setIsMonitoring] = useState(false);
    const [activeMode, setActiveMode] = useState<'lite' | 'full'>('lite');
    const [messageCount, setMessageCount] = useState(0);

    const activeSessions = [
        { id: 's1', name: 'Лекция по биологии' },
        { id: 's2', name: 'Семинар по химии' }
    ];

    const recentSessions = [
        { id: 's3', name: 'Вводная лекция' },
        { id: 's4', name: 'Практика по физике' }
    ];

    const handleStartMonitoring = () => {
        setIsMonitoring(true);
        // Здесь будет логика начала мониторинга
    };

    const handleStopMonitoring = () => {
        setIsMonitoring(false);
        // Здесь будет логика остановки мониторинга
    };

    const handleClearHistory = () => {
        setMessageCount(0);
        // Здесь будет логика очистки истории
    };

    return (
        <div className={styles.container}>
            {/* Боковая панель */}
            <div className={styles.sidebar}>
                <div className={styles.section}>
                    <div className={styles.subsectionTitle}><CogIcon /> Настройки мониторинга</div>
                </div>

                <div className={styles.section}>
                    <div className={styles.subsectionTitle}> Сервер</div>
                    <div className={styles.serverAddress}>51.250.115.73:8000</div>
                    <div className={styles.statusItem}>
                        <span>Статус</span>
                        <span className={styles.statusActive}><CheckCircleIcon /> Сервер доступен</span>
                    </div>
                    <div className={styles.statusItem}>
                        <span>MongoDB</span>
                        <span className={styles.statusActive}><CheckCircleIcon /></span>
                    </div>
                    <div className={styles.statusItem}>
                        <span>API ключи</span>
                        <span className={styles.statusActive}><CheckCircleIcon /></span>
                    </div>
                    <div className={styles.statusItem}>
                        <span>Активные сессии</span>
                        <span>0</span>
                    </div>
                    <div className={styles.statusItem}>
                        <span>WebSocket</span>
                        <span>4</span>
                    </div>
                </div>

                <div className={styles.section}>
                    <div className={styles.subsectionTitle}>📊 Производительность</div>
                    <div className={styles.statusItem}>
                        <span>Сообщений:</span>
                        <span className={styles.statusActive}>{messageCount}</span>
                    </div>
                </div>

                <div className={styles.section}>
                    <div className={styles.subsectionTitle}>📋 Выбор сессии</div>
                    <div className={styles.subsectionTitle}>🟢 Активные сессии:</div>
                    {activeSessions.length > 0 ? (
                        activeSessions.map(session => (
                            <div key={session.id} className={styles.modeOption}>
                                {session.name}
                            </div>
                        ))
                    ) : (
                        <div className={styles.emptyState}>Нет активных сессий</div>
                    )}

                    <div className={styles.subsectionTitle}>⚪ Последние завершенные:</div>
                    {recentSessions.length > 0 ? (
                        recentSessions.map(session => (
                            <div key={session.id} className={styles.modeOption}>
                                {session.name}
                            </div>
                        ))
                    ) : (
                        <div className={styles.emptyState}>Нет завершенных сессий</div>
                    )}
                </div>

                <div className={styles.section}>
                    <div className={styles.subsectionTitle}>🚀 Режим</div>
                    <div className={styles.modeSelector}>
                        <div
                            className={`${styles.modeOption} ${activeMode === 'lite' ? styles.modeOptionActive : ''}`}
                            onClick={() => setActiveMode('lite')}
                        >
                            <span className={styles.modeIcon}>🚀</span>
                            <div>
                                <div>Lite (20+ мин)</div>
                                <div className={styles.statusActive}><CheckCircleIcon /> Оптимизировано для долгих сессий</div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Основное содержимое */}
            <div className={styles.content}>
                <h1 className={styles.sectionTitle}>Мониторинг в реальном времени</h1>

                <div className={styles.section}>
                    <h2 className={styles.subsectionTitle}>Отслеживание событий обработки аудио</h2>
                    <div className={styles.buttonGroup}>
                        <button
                            onClick={handleStartMonitoring}
                            disabled={isMonitoring}
                            className={`${styles.button} ${styles.buttonPrimary}`}
                        >
                            Начать мониторинг
                        </button>
                        <button
                            onClick={handleStopMonitoring}
                            disabled={!isMonitoring}
                            className={`${styles.button} ${styles.buttonSecondary}`}
                        >
                            Остановить мониторинг
                        </button>
                    </div>
                    <p>
                        {isMonitoring
                            ? 'Идет мониторинг событий...'
                            : 'Начните мониторинг для просмотра событий в реальном времени'}
                    </p>
                </div>

                <div className={styles.section}>
                    <h2 className={styles.subsectionTitle}>Оптимизированный мониторинг:</h2>

                    <h3 className={styles.subsectionTitle}>Lite-режим для долгих лекций:</h3>
                    <ul className={styles.featureList}>
                        <li className={styles.featureItem}>Обновление каждые 3 секунды (экономия ресурсов)</li>
                        <li className={styles.featureItem}>Максимум 30 сообщений в памяти</li>
                        <li className={styles.featureItem}>Кнопка быстрой очистки истории</li>
                        <li className={styles.featureItem}>Упрощенный интерфейс без эмпакций</li>
                        <li className={styles.featureItem}>Удаление дубликатов сообщений</li>
                    </ul>

                    <h3 className={styles.subsectionTitle}>Как использовать:</h3>
                    <ul className={styles.featureList}>
                        <li className={styles.featureItem}>Выберите сессию в боковой панели</li>
                        <li className={styles.featureItem}>Нажмите "Начать мониторинг"</li>
                        <li className={styles.featureItem}>При долгих лекциях периодически нажимайте "✓ Очистить"</li>
                        <li className={styles.featureItem}>Следите за производительностью в боковой панели</li>
                    </ul>

                    <h3 className={styles.subsectionTitle}>Для лекций 20+ минут:</h3>
                    <ul className={styles.featureList}>
                        <li className={styles.featureItem}>Используйте Lite-режим</li>
                        <li className={styles.featureItem}>Очищайте историю каждые 10-15 минут</li>
                        <li className={styles.featureItem}>Следите за количеством сообщений в памяти</li>
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default MonitoringPage;
