import { useState } from 'react';
import { FiHeadphones, FiPlay, FiRefreshCw } from 'react-icons/fi';
import { FaStop } from 'react-icons/fa';
import styles from './StudentPage.module.css';

const StudentPage = () => {
    const [isListening, setIsListening] = useState(false);
    const [selectedLecture, setSelectedLecture] = useState<string | null>(null);

    const lectures = [
        { id: '1', title: 'Введение в биологию' },
        { id: '2', title: 'Основы химии' },
        { id: '3', title: 'Физика для начинающих' },
    ];

    const handleStartListening = () => {
        if (!selectedLecture) return;
        setIsListening(true);
    };

    const handleStopListening = () => {
        setIsListening(false);
    };

    const handleRefresh = () => {
        // Логика обновления
    };

    return (
        <div className={styles.container}>
            {/* ... (остальная разметка) ... */}

            <div className={styles.buttonGroup}>
                <button
                    onClick={handleStartListening}
                    disabled={!selectedLecture || isListening}
                    className={`${styles.button} ${styles.buttonPrimary}`}
                >
                    {/* <FiPlay className={styles.icon} /> */}
                    Начать прослушивание
                </button>

                <button
                    onClick={handleStopListening}
                    disabled={!isListening}
                    className={`${styles.button} ${styles.buttonDanger}`}
                >
                    {/* <FaStop className={styles.icon} /> */}
                    Остановить прослушивание
                </button>

                <button
                    onClick={handleRefresh}
                    className={`${styles.button} ${styles.buttonSecondary}`}
                >
                    {/* <FiRefreshCw className={styles.icon} /> */}
                    Обновить
                </button>
            </div>

            <div className={styles.content}>
                <div className={styles.card}>
                    <header className={styles.header}>
                        {/* <FiHeadphones className={styles.headerIcon} /> */}
                        <h1 className={styles.title}>Слушатель лекции</h1>
                    </header>

                    <p className={styles.description}>
                        Просмотр лекции в реальном времени (только чтение)
                    </p>

                    <div className={styles.buttonGroup}>
                        <button
                            onClick={handleStartListening}
                            disabled={!selectedLecture || isListening}
                            className={`${styles.button} ${styles.buttonPrimary}`}
                        >
                            {/* <FiPlay /> */}
                            Начать прослушивание
                        </button>

                        <button
                            onClick={handleStopListening}
                            disabled={!isListening}
                            className={`${styles.button} ${styles.buttonDanger}`}
                        >
                            {/* <FaStop /> */}
                            Остановить прослушивание
                        </button>

                        <button
                            onClick={handleRefresh}
                            className={`${styles.button} ${styles.buttonSecondary}`}
                        >
                            {/* <FiRefreshCw /> */}
                            Обновить
                        </button>
                    </div>

                    <div className={styles.lectureInfo}>
                        <p className={styles.lectureInfoText}>
                            {selectedLecture
                                ? `Выбрана лекция: "${lectures.find((l) => l.id === selectedLecture)?.title
                                }"`
                                : 'Выберите лекцию и нажмите "Начать прослушивание"'}
                        </p>
                    </div>
                </div>

                {/* ... остальной код ... */}
            </div>
        </div>
    );
};

export default StudentPage;
